import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Note } from '../../models/note.model';

@Component({
  selector: 'app-note-card',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './note-card.component.html',
  styleUrls: ['./note-card.component.css']
})
export class NoteCardComponent {
  @Input() note!: Note;
  @Output() onEdit = new EventEmitter<Note>();
  @Output() onDelete = new EventEmitter<number>();
  @Output() onPin = new EventEmitter<Note>();

  editNote(): void {
    this.onEdit.emit(this.note);
  }

  deleteNote(): void {
    if (this.note.id) {
      this.onDelete.emit(this.note.id);
    }
  }

  pinNote(): void {
    this.onPin.emit(this.note);
  }

  formatDate(date: string | undefined): string {
    if (!date) return '';
    const noteDate = new Date(date);
    
    return noteDate.toLocaleString([], { 
      year: 'numeric',
      month: 'short', 
      day: 'numeric'
    });
  }

  formatTime(date: string | undefined): string {
    if (!date) return '';
    const noteDate = new Date(date);
    
    return noteDate.toLocaleString([], { 
      hour: '2-digit', 
      minute: '2-digit',
      second: '2-digit'
    });
  }
}
<div class="note-card" [style.backgroundColor]="note.color">
  <div class="note-header">
    <h3 class="note-title">{{ note.title }}</h3>
    <button
      class="pin-btn"
      [class.pinned]="note.is_pinned"
      (click)="pinNote()"
      title="Pin note"
    >
      üìå
    </button>
  </div>

  <p class="note-content">{{ note.content }}</p>

  <div class="note-date">
    <div class="date-line">{{ formatDate(note.created_at) }}</div>
    <div class="time-line">{{ formatTime(note.created_at) }}</div>
  </div>

  <div class="note-actions">
    <button class="action-btn edit-btn" (click)="editNote()" title="Edit">
      ‚úèÔ∏è
    </button>
    <button class="action-btn delete-btn" (click)="deleteNote()" title="Delete">
      üóëÔ∏è
    </button>
  </div>
</div>
